import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { AuthGuard } from '../auth.guard';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css'],
})
export class SigninComponent implements OnInit {
  signForm!: FormGroup
  constructor(
    private router: Router,
    private userService: UserService,
    private auth: AuthGuard
  ) {}
  ngOnInit(): void {
    this.createRegistrationForm();
  }
  showLogin: boolean = false;
  SignUpUsers: any[] = [];
  signUpForm!: signUp;
  localData: string | null = '';
  dataParse: string | null = '';

  LogInUsers: any[] = [];
  loginForm!: logIn;

  openlogin() {
    this.showLogin = true;
  }

  openSignup() {
    this.showLogin = false;
  }
  signUp(data: signUp) {
    this.SignUpUsers.push(data);
    localStorage.setItem('SignUpUsers', JSON.stringify(this.SignUpUsers));
    this.localData = localStorage.getItem('SignUpUsers');
    if (this.localData !== null) {
      this.SignUpUsers = JSON.parse(this.localData);

    }
  }


  logIn(data: logIn) {
    this.LogInUsers.push(data);

    localStorage.setItem('logInUsers', JSON.stringify(this.LogInUsers));
    this.dataParse = localStorage.getItem('logInUsers');
    if (this.dataParse !== null) {
      this.LogInUsers = JSON.parse('logInUsers');
    }
  }
  createRegistrationForm(){
    this.signForm = new FormGroup({
      "firstName":new FormControl('',[Validators.required,Validators.minLength(2),Validators.maxLength(10),Validators.pattern("[a-zA-Z]{2,10}")]),
      "email":new FormControl('',Validators.required,[]),

    })
  }
  save(){
    console.log("form data",this.signForm.value);
  }

  get firstName (){
    return this.signForm.controls['firstName'];

  }
  get email (){
    return this.signForm.controls['email']
  }
}

class signUp {
  name!: string;
  email!: string;
  password!: string;
  get: any;
  controls: any;
}
class logIn {
  email!: string;
  password!: string;
}
<div class="seller-auth" *ngIf="!showLogin">
    <form #signUpForm="ngForm" [formGroup]="signForm" (ngSubmit)="signUp(signUpForm.value)" (ngSubmit)="save()" >
        <h3 class="text-center" > Sign-Up</h3>
        <div class="mb-3">
            <label for="name" class="form-label"> Name</label>
            <input type="email" class="form-control" id="name" placeholder="enter name" name="name" formControlName="firstName" ngModel />
            <div *ngIf="firstName.touched && firstName.invalid">
                <div class="invalid-feedback d-block" *ngIf="firstName.errors?.['required']">
                    It is required field
                </div>
                <div class="invalid-feedback d-block" *ngIf="firstName.errors?.['pattern']">
                   Please enter the valid name
                </div>
            </div>
        </div>
        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Email address</label>
            <input type="email" class="form-control" id="exampleInputEmail1" placeholder="enter email" name="email" formControlName="email"
                ngModel />
        </div>
        <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">Password</label>
            <input type="password" class="form-control" id="exampleInputPassword1" placeholder="enter password"
                name="password" ngModel />
        </div>
        <button class="submit" class="btn btn-primary"  routerLink="/app-add-task">SignUp</button> <br />
        <a class="auth-toggle-link" (click)="openlogin()">Already Have Account? Click To Login</a>
    </form>
</div>

<!-- Login Area -->
<div class="seller-auth" *ngIf="showLogin">
    <form #loginForm="ngForm" (ngSubmit)="logIn(loginForm.value)">
        <h3 class="text-center"> Login</h3>
        <p></p>
        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Email address</label>
            <input type="email" class="form-control" id="exampleInputEmail1" placeholder="enter email" name="email"
                ngModel />
        </div>
        <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">Password</label>
            <input type="password" class="form-control" id="exampleInputPassword1" placeholder="enter password"
                name="password" ngModel />
        </div>
        <button type="submit" class="btn btn-primary"  routerLink="/app-add-task">Login</button> <br />

        <a class="auth-toggle-link" (click)="openSignup()">
            Don't Have Account? Click To SignUp</a>
    </form>
</div>
