import { Component } from '@angular/core';
import { Route, Router } from '@angular/router';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css'],
})
export class SigninComponent {


  constructor(private router: Router, private userService:UserService) { }
  showLogin: boolean = false;
  SignUpUsers:any[]=[];
  signUpForm!: signUp;
  localData: string | null = '';
  dataParse:string | null='';

  LogInUsers:any[]=[];
  loginForm!:logIn

  openlogin() {
    this.showLogin = true;
  }

  openSignup() {
    this.showLogin = false;
  }
  signUp(data:signUp){
    this.SignUpUsers.push(data)
   localStorage.setItem('SignUpUsers', JSON.stringify(this.SignUpUsers)); 
   this.localData=localStorage.getItem('SignUpUsers')
   if (this.localData !== null) {
    this.SignUpUsers = JSON.parse(this.localData);

  }  
  }

  logIn(data:logIn){
     this.LogInUsers.push(data);
     localStorage.setItem('logInUsers',JSON.stringify(this.LogInUsers));
     this.dataParse=localStorage.getItem('logInUsers')
     if(this.dataParse !== null){
      this.LogInUsers= JSON.parse('logInUsers')

      this.router.navigate(['/task-list'])

    }
     }
    //  this.userService.login(data);

}

class signUp{
  name!:string
  email!:string
  password!:string;
  get: any;
}
class logIn{

  email!:string;
  password!:string;
}
