@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent {

   openLogin:boolean=true;

   openlogin(){
     this.openLogin = true;
   }

   openSignup(){
    this.openLogin = false;
  }

}
